import { ChakraProvider, Box, Heading, Input, Button, Center,  } from '@chakra-ui/react';
import React, { useState } from 'react';
import axios from 'axios';
import LocationInput from './components/LocationInput';
import WavesTideInfo from './components/WavesTideInfo';

const App: React.FC = () => {
  const [wavesTideData, setWavesTideData] = useState<any | null>(null);
  const [error, setError] = useState<string>('');

  const handleLocationSubmit = async (location: string) => {
    try {
      setError('');
      const response = await axios.get(`http://localhost:5000/api/waves-tide-info?location=${encodeURIComponent(location)}`);
      setWavesTideData(response.data);
    } catch (error) {
      setError('Error fetching waves and tide information.');
      setWavesTideData(null);
    }
  };

  return (
    <ChakraProvider>
      <Box bg="gray.100" minHeight="100vh" py={8}>
        <Center>
          <VStack spacing={4} p={4} bg="white" boxShadow="md" borderRadius="lg">
            <Heading as="h1" size="lg">
              Waves & Tide App
            </Heading>
            <LocationInput onLocationSubmit={handleLocationSubmit} />
            {error && <Text color="red.500">{error}</Text>}
            {wavesTideData && <WavesTideInfo wavesTideData={wavesTideData} />}
          </VStack>
        </Center>
      </Box>
    </ChakraProvider>
  );
};

export default App;
