import { ChakraProvider, Box, Heading, Text } from '@chakra-ui/react';
import React, { useState } from 'react';
import axios from 'axios';
import LocationInput from './components/LocationInput';
import WindInfo from './components/WindInfo';

const App: React.FC = () => {
  const [windInfo, setWindInfo] = useState<any | null>(null);
  const [error, setError] = useState<string>('');

  const handleLocationSubmit = async (zipCode: string) => {
    try {
      setError('');
      const response = await axios.get(`http://localhost:5000/api/wind-info?zipCode=${zipCode}`);
      setWindInfo(response.data);
    } catch (error) {
      setError('Error fetching wind information.');
      setWindInfo(null);
    }
  };

  return (
    <ChakraProvider>
      <Box bg="gray.100" minHeight="100vh" py={8}>
        <Box maxW="md" mx="auto" p={4} bg="white" boxShadow="md" borderRadius="lg">
          <Heading as="h1" size="lg" textAlign="center">
            Wind Info App
          </Heading>
          <LocationInput onLocationSubmit={handleLocationSubmit} />
          {error && <Text color="red.500">{error}</Text>}
          {windInfo && <WindInfo windInfo={windInfo} />}
        </Box>
      </Box>
    </ChakraProvider>
  );
};

export default App;
