import { ChakraProvider } from '@chakra-ui/react';
import { useState } from 'react';
import LocationInput from './components/LocationInput';
import WavesTideInfo from './components/WavesTideInfo';

const App: React.FC = () => {
  const [location, setLocation] = useState('');
  const [wavesTideData, setWavesTideData] = useState<any | null>(null);

  const handleLocationSubmit = (location: string) => {
    // When the location is submitted, update the state with the entered location

    setLocation(location);
  };

  return (
    <ChakraProvider>
      <div>
        <h1>Waves & Tide App</h1>
        <LocationInput onLocationSubmit={handleLocationSubmit} />
        {/* Show the WavesTideInfo component with the data from the backend */}
        {location && <WavesTideInfo location={location} />}
      </div>
    </ChakraProvider>
  );
};

export default App;
