import { ChakraProvider, Box, Heading, Text } from '@chakra-ui/react';
import React, { useState } from 'react';
import axios from 'axios';
import LocationInput from './components/LocationInput';
import WavesTideInfo from './components/WavesTideInfo';

const App: React.FC = () => {
  const [wavesTideData, setWavesTideData] = useState<any | null>(null);
  const [error, setError] = useState<string>('');

  const handleLocationSubmit = async (latitude: number, longitude: number) => {
    try {
      setError('');
      const response = await axios.get(`http://localhost:5000/api/waves-tide-info?latitude=${latitude}&longitude=${longitude}`);
      setWavesTideData(response.data);
    } catch (error) {
      setError('Error fetching waves and tide information.');
      setWavesTideData(null);
    }
  };

  return (
    <ChakraProvider>
      <Box bg="gray.100" minHeight="100vh" py={8}>
        <Box maxW="md" mx="auto" p={4} bg="white" boxShadow="md" borderRadius="lg">
          <Heading as="h1" size="lg" textAlign="center">
            Waves & Tide App
          </Heading>
          <LocationInput onLocationSubmit={handleLocationSubmit} />
          {error && <Text color="red.500">{error}</Text>}
          {wavesTideData && <WavesTideInfo wavesTideData={wavesTideData} />}
        </Box>
      </Box>
    </ChakraProvider>
  );
};

export default App;
