import { ChakraProvider, Box, Heading, Text } from '@chakra-ui/react';
import React, { useState } from 'react';
import axios from 'axios';
import LocationInput from './components/LocationInput';
import WavesTideInfo from './components/WavesTideInfo';

const App: React.FC = () => {
  const [wavesTideData, setWavesTideData] = useState<any | null>(null);
  const [error, setError] = useState<string>('');

  const handleLocationSubmit = async (zipCode: string) => {
    try {
      setError('');
      const response = await axios.get(`http://localhost:5000/api/waves-tide-info?zipCode=${zipCode}`);
      setWavesTideData(response.data);
    } catch (error) {
      setError('Error fetching waves and tide information.');
      setWavesTideData(null);
    }
  };

  // Rest of the code remains the same
};
