// LocationInput.tsx
import { useState } from 'react';

const LocationInput: React.FC<{ onLocationSubmit: (zipCode: string) => void }> = ({ onLocationSubmit }) => {
  const [zipCode, setZipCode] = useState('');

  const handleSubmit = () => {
    if (zipCode) {
      onLocationSubmit(zipCode);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={zipCode}
        onChange={(e) => setZipCode(e.target.value)}
        placeholder="Enter zip code..."
      />
      <button onClick={handleSubmit}>Get Waves & Tide Info</button>
    </div>
  );
};

export default LocationInput;
